import Sketch from "react-p5";

// Appコンポーネントの定義
function App() {
  return (
    <div className="App">
      <Sketch setup={setup} draw={draw} />
    </div>
  );


  const setup = (p5, canvasParentRef) => {
    p5.createCanvas(500, 500).parent(canvasParentRef);
  }

  const draw = (p5) => {
    p5.background(0);
    p5.ellipse(x, y, 70, 70);
    x++;
  }
}

// Appコンポーネントを他のファイルで利用できるようにエクスポート
export default App;
